import React, { useState } from 'react';
import Cookies from 'universal-cookie';
import axios from 'axios';

import signInImage from '../assets/login.png';


const Auth = () => {
  const [isSignup, setIsSignup] = useState(true);

  const handleChange = () => {

  };

  const switchMode = () => {
